import 'package:flutter/material.dart';
import 'package:path/path.dart';
import 'package:sqflite/sqflite.dart';
import 'package:sqflite/sqlite_api.dart';
import 'package:untitled1/recipe.dart';

class RecipeDataBase {
  RecipeDataBase._();
  static final RecipeDataBase instance = RecipeDataBase._();
  static final Database _database;

  Future<Database> get dataBase async {
    if (_database != null) return _database;
    _database = await initDB();
    return _database;
  }
//Initialisation et la création de la base de données

  initDB() async {
    WidgetsFlutterBinding.ensureInitialized();
    return await openDatabase(
        join(await getDatabasesPath(), 'recipe_database.db'),
        onCreate: (db, version) {
      return db.execute(
          "CREATE TABLE recipe(title TEXT PRIMARY KEY,  user TEXT, img TEXT, description TEXT, isFavorited INTEGER, favoriteCount INTEGER) ");
    }, version: 1);
  }

  //LA methode qui permet d'inserer dans une base de données SQFlite
  void insertRecipe(Recipe recipe) async {
    final Database db = await dataBase;

    await db.insert('recipe', recipe.toMap(),
        conflictAlgorithm: ConflictAlgorithm.replace);
  }

  //LA methode qui permet de modifier des elements dans une base de données SQFlite
  void updateRecipe(Recipe recipe) async {
    final Database db = await dataBase;
    await db.update("recipe", recipe.toMap(),
        where: "title=?", whereArgs: [recipe.title]);
  }

  //LA methode qui permet de supprimer des elements dans une base de données SQFlite
  void deleteRecipe(String title) async {
    final Database db = await dataBase;
    db.delete("recipe", where: "title=?", whereArgs: [title]);
  }

  Future<List<Recipe>> recipes() async {
    final Database db = await dataBase;
    final List<Map<String, dynamic>> maps = await db.query('recipe');
    List<Recipe> recipes = List.generate(maps.length, (i) {
      return Recipe.fromMap(maps[i]);
    });
    if (recipes.isEmpty) {
      for (Recipe recipe in defaultRecipes) {
        insertRecipe(recipe);
      }
      recipes = defaultRecipes;
    }
    return recipes;
  }

